"use strict";(self.webpackChunkwos_wiki=self.webpackChunkwos_wiki||[]).push([[6908],{784:e=>{e.exports=JSON.parse('{"tag":{"label":"Weapons","permalink":"/Pilot.lua-Luau-LSP/docs/tags/weapons","allTagsPath":"/Pilot.lua-Luau-LSP/docs/tags","count":15,"items":[{"id":"Objects/AutomaticLaser","title":"AutomaticLaser","description":"Fires a continous set of lasers when powered. Like most other energy/thermal weapons, it generates heat when powered. It generates up to 65 degrees of heat, meaning you will need atleast 2 or 3 coolers.","permalink":"/Pilot.lua-Luau-LSP/docs/Objects/AutomaticLaser"},{"id":"Objects/BurstLaser","title":"BurstLaser","description":"Fires 5 rapid sets of concentrated laser beams. Each beam does a mediate amount of damage.","permalink":"/Pilot.lua-Luau-LSP/docs/Objects/BurstLaser"},{"id":"Objects/Cannon","title":"Cannon","description":"Fires a cannon ball when triggered or clicked.","permalink":"/Pilot.lua-Luau-LSP/docs/Objects/Cannon"},{"id":"Objects/EnergyBomb","title":"EnergyBomb","description":"Causes a powerful explosion when triggered. Often used in space combat as a means of a powerful missile or torpedo.","permalink":"/Pilot.lua-Luau-LSP/docs/Objects/EnergyBomb"},{"id":"Objects/EnergyGun","title":"EnergyGun","description":"Fires a powerful shot of energy when provided power and triggered.","permalink":"/Pilot.lua-Luau-LSP/docs/Objects/EnergyGun"},{"id":"Objects/Explosive","title":"Explosive","description":"Causes an explosion when triggered. You can use a delay wire to make it explode after a few seconds and not immediately.","permalink":"/Pilot.lua-Luau-LSP/docs/Objects/Explosive"},{"id":"Objects/Fireworks","title":"Fireworks","description":"Causes an explosion when triggered. You can use a delay wire to make it explode after a few seconds and not immediately.","permalink":"/Pilot.lua-Luau-LSP/docs/Objects/Fireworks"},{"id":"Objects/Gun","title":"Gun","description":"Fires a harmful bullet when triggered.","permalink":"/Pilot.lua-Luau-LSP/docs/Objects/Gun"},{"id":"Objects/ImpulseCannon","title":"ImpulseCannon","description":"Blasts a harmless burst of energy which sends anything nearby soaring at a high velocity.","permalink":"/Pilot.lua-Luau-LSP/docs/Objects/ImpulseCannon"},{"id":"Objects/Laser","title":"Laser","description":"Fires a concentraded beam of energy when powered, which can be used to damage and set objects on fire.","permalink":"/Pilot.lua-Luau-LSP/docs/Objects/Laser"},{"id":"Objects/PlasmaCannon","title":"PlasmaCannon","description":"Fires an orb of unstable plasma out of the barrel exploding on impact.","permalink":"/Pilot.lua-Luau-LSP/docs/Objects/PlasmaCannon"},{"id":"Objects/Railgun","title":"Railgun","description":"Fires a piercing rail at high speeds. A long range weapon. Requires rails and electricity for ammo. Damage increases for every 10 studs of length.pierce if the hit part is destroyed. Must be at least have a width and height of 6 and at least a length of 30 to function.","permalink":"/Pilot.lua-Luau-LSP/docs/Objects/Railgun"},{"id":"Objects/Shotgun","title":"Shotgun","description":"Fires a shot of harmful pellets when triggered.","permalink":"/Pilot.lua-Luau-LSP/docs/Objects/Shotgun"},{"id":"Objects/Warhead","title":"Warhead","description":"Causes a powerful explosion when triggered. You can use a delay wire to make it explode after a few seconds and not immediately.","permalink":"/Pilot.lua-Luau-LSP/docs/Objects/Warhead"},{"id":"Objects/ZapWire","title":"ZapWire","description":"Allows electrical objects to interact with each other when connected by wires.","permalink":"/Pilot.lua-Luau-LSP/docs/Objects/ZapWire"}],"unlisted":false}}')}}]);